import axios from 'axios'
import React , {useState} from 'react'
import "./login.css"

const Login = () => {

    const [user,setUser]=useState({
        email:"",
        password:""
        }) 

    const handleChange = e =>{
        const {name , value} = e.target;
        setUser({
            ...user,
            [name]:value
        })
    }

    const login = () =>{
        axios.post("https://localhost:9002/login",user)
        .then(res=>alert(res.d))
    }
    return (
        <div className='login'>
        {console.log()}
            <h1>Login</h1>
            <input type="text" name="email" value={user.email}  onChange={handleChange}  placeholder='Enter Mail'/>
            <hr />
            <input type="password" name="password" value={user.password}  onChange={handleChange} placeholder='Enter password' />
            <div className='button' >Login</div>
            <h2>or</h2>
            <div className='button' onClick={login}>Register</div>
        </div>

    )
}

export default Login;
